package ptpl;

public class Elseif {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int a = 2;

		if (a == 1) 
		{
			System.out.println("One");
		} 
		else if (a == 2) 
		{
			System.out.println("Two");
		} 	
		else if (a == 3) 
		{
			System.out.println("Three");
		} 
		else 
		{
			System.out.println("invalid Value.");

	}

}
}
