package ptpl;

public class EvenOddTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
 
		int n;

		n = 23;

		if( n % 2 == 0 )
			System.out.println("Number " + n + " is Even Number." );
		else
			System.out.println("Number " + n + " is Odd Number." );
	}

}
