package ptpl;

public class Max2IfElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	//  Program to find Maximum of Two numbers using If-Else.
		
		int a, b, max;

		a = 23;
		b = 14;

		if( a > b )
			max = a;
		else
			max = b;
	    
		System.out.println("\nMaximum : " + max );


	}

}
