package ptpl;

public class SimpleIf2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 
		 int a = 10;
	        int b = 20;
	        
	        if(a>b){
	            System.out.println("a is greater than b");
	        }
	        
	        if(a<b){
	            System.out.println("a is less than b");
	        }
	        
	        if(a==b){
	            System.out.println("a is equal to b");
	        }

	}

}
